import React, { Component } from "react";
import TutorialDataService from "../services/recipe.service";
export default class AddTutorial extends Component {
  constructor(props) {
    super(props);
    this.onCookingInstruction = this.onCookingInstruction.bind(this);
    this.onDishType = this.onDishType.bind(this);
    this.saveRecipe = this.saveRecipe.bind(this);
    this.onIngredients=this.onIngredients.bind(this);
    this.onPortionSize=this.onPortionSize.bind(this);

    this.newRecipe = this.newRecipe.bind(this);
    this.state = {
      id: null,
      cookingInstructions: "",
      dishType: "", 
      ingredients:"",
      portionSize:0,
      published: false,
      submitted: false
    };
  }
  onCookingInstruction(e) {
    this.setState({
      title: e.target.value
    });
  }
  onDishType(e) {
    this.setState({
      description: e.target.value
    });
  }
  onIngredients(e) {
    this.setState({
      description: e.target.value
    });
  }
  onPortionSize(e) {
    this.setState({
      description: e.target.value
    });
  }

  saveRecipe() {
    var data = {
      title: this.state.title,
      description: this.state.description
    };
    TutorialDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          title: response.data.title,
          description: response.data.description,
          published: response.data.published,
          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }
  newRecipe() {
    this.setState({
      id: null,
      title: "",
      description: "",
      published: false,
      submitted: false
    });
  }
  render() {
    return (
        <div className="submit-form">
          {this.state.submitted ? (
            <div>
              <h4>You submitted successfully!</h4>
              <button className="btn btn-success" onClick={this.newTutorial}>
                Add
              </button>
            </div>
          ) : (
            <div>
              <div className="form-group">
                <label htmlFor="cookinstruction">Cooking Instruction</label>
                <input
                  type="text"
                  className="form-control"
                  id="cookinstruction"
                  required
                 // value={this.state.cookingInstructions}
                 // onChange={this.onCookingInstruction}
                  name="cookinstruction"
                />
              </div>
              <div className="form-group">
                <label htmlFor="dishtype">Dish Type</label>
                <input
                  type="text"
                  className="form-control"
                  id="dishtype"
                  required
                  value={this.state.dishType}
                  onChange={this.onDishType}
                  name="dishtype"
                />
              </div>
              <div className="form-group">
                <label htmlFor="ingredients">Ingredients</label>
                <input
                  type="text"
                  className="form-control"
                  id="ingredients"
                  required
                  value={this.state.ingredients}
                  onChange={this.onIngredients}
                  name="ingredients"
                />
              </div>
              <div className="form-group">
                <label htmlFor="portionsize">Portion Size</label>
                <input
                  type="text"
                  className="form-control"
                  id="portionsize"
                  required
                  value={this.state.portionSize}
                  onChange={this.onPortionSize}
                  name="portionsize"
                />
              </div>

              <button onClick={this.saveRecipe} className="btn btn-success">
                Submit
              </button>
            </div>
          )}
        </div>
      );
  }
}
