import React, { Component } from "react";
import TutorialDataService from "../services/recipe.service";
import { Link } from "react-router-dom";

export default class TutorialsList extends Component {
  constructor(props) {
    super(props);
    this.onChangeSearchTitle = this.onChangeSearchTitle.bind(this);
    this.retrieveTutorials = this.retrieveTutorials.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActiveTutorial = this.setActiveTutorial.bind(this);
    this.searchTitle = this.searchTitle.bind(this);
    this.state = {
      tutorials: [],
      currentTutorial: null,
      currentIndex: -1,
      searchTitle: ""
    };
  }
  componentDidMount() {
    this.retrieveTutorials();
  }
  onChangeSearchTitle(e) {
    const searchTitle = e.target.value;
    this.setState({
      searchTitle: searchTitle
    });
  }
  retrieveTutorials() {
    TutorialDataService.getAll()
      .then(response => {
        this.setState({
          tutorials: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }
  refreshList() {
    this.retrieveTutorials();
    this.setState({
      currentTutorial: null,
      currentIndex: -1
    });
  }
  setActiveTutorial(tutorial, index) {
    this.setState({
      currentTutorial: tutorial,
      currentIndex: index
    });
  }

  searchTitle() {
    TutorialDataService.findByTitle(this.state.searchTitle)
      .then(response => {
        this.setState({
          tutorials: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }
  render() {
    const { searchTitle, tutorials, currentTutorial, currentIndex } = this.state;
    return (
      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by Cooking Instruction"
              value={searchTitle}
              onChange={this.onChangeSearchTitle}
            />
            <div className="input-group-append">
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={this.searchTitle}
              >
                Search
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>Recipe List</h4>
          <ul className="list-group">
            {tutorials &&
              tutorials.map((tutorial, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActiveTutorial(tutorial, index)}
                  key={index}
                >
                  {tutorial.id} - {tutorial.dishName}
                </li>
              ))}
          </ul>

        </div>
        <div className="col-md-6">
          {currentTutorial ? (
            <div>
              <h4>Recipe</h4>
              <div>
                <label>
                  <strong>Recipe ID:</strong>
                </label>{" "}
                {currentTutorial.id}
              </div>
              <div>
                <label>
                  <strong>Dish Name:</strong>
                </label>{" "}
                {currentTutorial.dishName}
              </div>
              <div>
                <label>
                  <strong>Dish Type:</strong>
                </label>{" "}
                {currentTutorial.dishType}
              </div>
              <div>
                <label>
                  <strong>Ingredients :</strong>
                </label>{" "}
                {currentTutorial.ingredients}
              </div>
              <div>
                <label>
                  <strong>Portion Size :</strong>
                </label>{" "}
                {currentTutorial.portionSize}
              </div>
              <div>
                <label>
                  <strong>Cooking Instructions :</strong>
                </label>{" "}
                {currentTutorial.cookingInstructions}
              </div>
              <div>
                <label>
                  <strong>Created Date :</strong>
                </label>{" "}
                {currentTutorial.createDate}
              </div>
              <Link
                to={"/recipes/" + currentTutorial.id}
                className="badge alert-warning">
                Edit
              </Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a Recipe...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
